def AudioPipe(path):
    items = get_audio_files(path)
    sp = SiamesePiar(items, items.map(parent_label))
    return Pipeline([sp, AudioMono.create, Resample(22050), Clip(5), Mixer, Spectify(), Normalize(), Decibelify(), Group()])